var Objects=require('./Objects.json');
describe("to select the ", function() {
	
	it("celsius", function() {
		
		browser.get(Objects.testSiteurl);
		//celsius
		element(by.xpath(".//*[@id='wx-header-wrap']/div/div/div/div[2]/div[1]/div/div/ul/li[3]/span[1]/a/span[1]/span[2]")).click();
		element(by.xpath(".//*[@id='wx-header-wrap']/div/div/div/div[2]/div[1]/div/div/ul/li[3]/span[1]/ul/li[1]/div/button[2]")).click();
		
	});
		it("country & to select the weather dropdown", function() {
		browser.get(Objects.testSiteurl);
		//country
		element(by.xpath(".//*[@id='wx-header-wrap']/div/div/div/div[2]/div[1]/div/div/ul/li[3]/span[1]/a/span[1]/span[2]")).click();
		element(by.xpath(".//*[@id='wx-header-wrap']/div/div/div/div[2]/div[1]/div/div/ul/li[3]/span[1]/ul/li[2]/div/div/ul/li[3]/div/span[1]")).click();
		element(by.xpath(".//*[@id='wx-header-wrap']/div/div/div/div[2]/div[1]/div/div/ul/li[3]/span[1]/ul/li[2]/div/div/ul/li[2]/ul/li[8]")).click();
		//browser.sleep(5000);
		//searching
		element(by.xpath(".//*[@id='wx-header-wrap']/div/div/div/div[2]/div[2]/div/section/div/form/input")).sendKeys("Bangalore, Karnataka, India");	
		//browser.sleep(5000);
	    element(by.xpath(".//*[@id='wx-header-wrap']/div/div/div/div[2]/div[2]/div/section/div/div[1]")).click();	  
		//browser.sleep(5000);
		element(by.xpath(".//*[@id='js-nowcard']/div[1]")).getText().then(function(value) {
		console.log(value);
		expect(value).toContain('BANGALORE, INDIA');
		 	
	   });
		
		//dropdown
		browser.actions().mouseMove(element(by.xpath(".//*[@id='mini-panel-header_drilldown_mini_in']/div/div/div/div[1]/ul/li[1]/a/span"))).perform();
		element(by.xpath(".//*[@id='mini-panel-header_drilldown_mini_in']/div/div/div/div[1]/ul/li[1]/ul/li[2]/a")).click();	
		element(by.xpath(".//*[@id='wx-main']/div[1]/div/a/span[1]")).click();
		//browser.sleep(5000);

		//maps
		element(by.xpath(".//*[@id='mini-panel-header_drilldown_mini_today_in']/div/div/div/div[1]/ul/li[2]/a/span")).click();
		element(by.xpath(".//*[@id='wx-footer']/div/div/div/div/div/div[2]/div/ul[1]/li[1]/a/strong")).click();
		element(by.xpath(".//*[@id='wx-main']/div[1]/article/div/header/h2")).getText().then(function(value) {
		console.log(value);
		expect(value).toEqual('RADAR & MAPS');
		
       });	
		
   }, 500000);
});